"""
Many of these functions are similar to those in matrixgen.py, 
but modified to generate counts of individual amino acids.
"""

sub_csv = 'C:/Users/yolan/Documents/Holmes/prot_align/matrices/sub_matrix.csv'
path_to_sup = 'C:/Users/yolan/SABmark/SABmark/sup/'
generate_matrix = True

matrix = {}
seqs_parsed = 0
expected_parsed = 0

# Generates a dictionary containing then number of distinct sequences in each group
# {group number : nseq}
def gen_nseq():
    nseq = {}
    setSummary = Path(path_to_sup).glob('set.summary').__iter__()
    summaryIter = open(setSummary.__next__())
    summaryIter.__next__()
    for line in summaryIter:
        line = line.split('\t')[:2]
        group = line[0]
        num = line[1]
        if group not in nseq.keys():
            nseq[int(group)] = int(num)
    return nseq

# Gets the protein sequences from a file, 
# returns as an array of two strings.
def get_seqs(file):
    iter = open(file).__iter__()
    line = iter.__next__()
    seqs = []
    seq = ""
    while iter:
        if ">" in line:
            if seq != "":
                seqs.append(seq)
                seq = ""
            line = iter.__next__()
        else:
            seq += line[:len(line)-1]
            try:
                line = iter.__next__()
            except:
                StopIteration
                seqs.append(seq)
                break
    if len(seq[0]) != len(seq[1]):
        return Exception("Alignment sequences different lengths")
    return seqs

# Calculates the count of amino acids present
def bgd_freq(seq):
    global matrix
    for i in range(len(seq)):
        p = seq[i]
        if p != '-':
            if p in matrix.keys():
                matrix[p] += 1
            else:
                matrix[p] = 1
    return matrix

# Applies the bgd_freq matrix to a file.
def apply_matrix(file):
    global matrix
    seqs = get_seqs(file)
    for i in range(len(seqs)):
        bgd_freq(seqs[i])
    return len(seqs)

# Iterates through specified group range to generate the matrix
def matrix_generator(bool):
    if not bool:
        return
    global matrix
    global seqs_parsed
    global expected_parsed
    begin = 1
    end = 425
    for group in range(begin, end + 1):
        print("Analyzing... %d/%d complete"%(group, end))
        seqs_parsed += apply_matrix(path_to_sup + 'group' + str(group) + '/group.fasta')
        expected_parsed += nseq[group]
        assert seqs_parsed == expected_parsed


# Generates the count of individual amino acids
nseq = gen_nseq()
