"""
csv_aggregator function from matrixgen.py, modified 
to generate counts of individual amino acids.
"""

sub_matrix = {}

def csv_aggregator():
    global sub_matrix
    csv_reader.__next__()
    for row in csv_reader:
        aa1 = row[1]
        aa2 = row[3]
        count = int(row[4])
        aa = aa1 + aa2
        if aa not in sub_matrix.keys():
            sub_matrix[aa] = 1
        else:
            sub_matrix[aa] += count

csv_reader = csv.reader(open(sub_csv))
csv_aggregator()
